def.body:
payload = Cable::Payload.new(message)
if payload.command == "subscribe"
  return subscribe(payload)
end
if payload.command == "unsubscribe"
  return unsubscribe(payload)
end
if payload.command == "message"
  return message(payload)
end

if !(@channels.has_key?(identifier))
  @channels[identifier] = [] of Cable::Channel
end
@channels[identifier] << channel
request = Redis::Request.new
request << "subscribe"
request << identifier
redis_subscribe._connection.send(request)
