@channel_mutex.synchronize do
  if @channels.has_key?(identifier)
    @channels[identifier].delete(channel)

    if @channels[identifier].size == 0
      backend.unsubscribe(identifier)

      @channels.delete(identifier)
    end
  else
    backend.unsubscribe(identifier)
  end
